mapper.update:
update tbl_donate
set title = #{title},
	content = #{content}
where dno = #{dno}

update tbl_donate_round
set goalamount = #{goalamount},
	voiddate = #{voiddate},
	status = #{status}
where drno = #{drno}

mapper.select:
select r.*, (select sum(amount) from tbl_donate_action where drno = #{drno}) nowamount  
from tbl_donate_round r 
where drno = #{drno}
