mapper.delete:
delete from tbl_like
where mno = #{mno}
AND (
(#{bno} IS NOT NULL AND bno = #{bno}) OR
(#{rno} IS NOT NULL AND rno = #{rno})
)

mapper.select:
SELECT COUNT(*) FROM tbl_like
WHERE mno = #{mno}
AND (
(#{bno} IS NOT NULL AND bno = #{bno}) OR
(#{rno} IS NOT NULL AND rno = #{rno})
)

SELECT COUNT(*) > 0
FROM tbl_like
WHERE bno = #{bno} AND mno = #{mno}
